SELECT COUNT(DRVER.MBER_ID) as TOTAL FROM DRVER_INFO DRVER LEFT JOIN (SELECT CARALC.VHCLE_NO, RCEPT.ALOC_ADRES, CARALC.RESVE_DT FROM ( SELECT VHCLE_NO, CNTER_CD, MAX(RESVE_DT) AS RESVE_DT FROM CARALC_HIST WHERE VHCLE_NO IS NOT NULL
AND CNTER_CD = #{cnter_cd}
GROUP BY VHCLE_NO, CNTER_CD ) CARALC , RCEPT_HIST RCEPT WHERE CARALC.CNTER_CD = RCEPT.CNTER_CD AND CARALC.RESVE_DT = RCEPT.RESVE_DT ) RCEPT ON DRVER.VHCLE_NO = RCEPT.VHCLE_NO , CNTER_INFO CNTER , MBER_INFO MBER , (SELECT DRVER.MBER_ID, DRVER.WHEELCHAIR_FIXING_STTUS_CD, DRVER.LC_CRDNT_X AS LON, DRVER.lC_CRDNT_Y AS LAT FROM ( SELECT MBER_ID, MAX(COLCT_DT) AS COLCT FROM DRVER_LC_HIST WHERE 1=1
AND CNTER_CD = #{cnter_cd}
GROUP BY MBER_ID ) DRVER_LC , DRVER_LC_HIST DRVER WHERE DRVER.MBER_ID = DRVER_LC.MBER_ID AND DRVER_LC.COLCT = DRVER.COLCT_DT )DRVER_LC WHERE DRVER.CNTER_CD = CNTER.CNTER_CD AND DRVER.MBER_ID = MBER.MBER_ID AND DRVER.MBER_ID = DRVER_LC.MBER_ID
AND DRVER.CNTER_CD = #{cnter_cd}
AND DRVER.DRVER_STTUS_CD = #{drverSttusCd}
AND MBER.MBER_NM LIKE CONCAT('%', #{drver_name}, '%')
ORDER BY FIELD (DRVER.DRVER_STTUS_CD ,'30', '00', '10', '20', '40'), RCEPT.RESVE_DT DESC

SELECT Y.* FROM ( SELECT X.*, @rownum:=@rownum+1 as rnum FROM ( SELECT LEFT(CNTER.CNTER_NM, 2) as cnter_nm , MBER.MBER_NM , RCEPT.ALOC_ADRES , DRVER.VHCLE_NO , (SELECT CD_VALUE_DE FROM CMMN_CD_VALUE_INFO WHERE CD_VALUE = DRVER.DRVER_STTUS_CD AND CD_ID = 'DRVER_STTUS_CD') AS DRVER_STTUS_NM , DRVER.MBER_ID , DRVER.CNTER_CD , DRVER.DRVER_STTUS_CD , DRVER_LC.WHEELCHAIR_FIXING_STTUS_CD , DRVER_LC.LON , DRVER_LC.LAT FROM DRVER_INFO DRVER LEFT JOIN (SELECT CARALC.VHCLE_NO, RCEPT.ALOC_ADRES, CARALC.RESVE_DT FROM ( SELECT VHCLE_NO, CNTER_CD, MAX(RESVE_DT) AS RESVE_DT FROM CARALC_HIST WHERE VHCLE_NO IS NOT NULL
AND CNTER_CD = #{cnter_cd}
GROUP BY VHCLE_NO, CNTER_CD ) CARALC , RCEPT_HIST RCEPT WHERE CARALC.CNTER_CD = RCEPT.CNTER_CD AND CARALC.RESVE_DT = RCEPT.RESVE_DT ) RCEPT ON DRVER.VHCLE_NO = RCEPT.VHCLE_NO , CNTER_INFO CNTER , MBER_INFO MBER , (SELECT DRVER.MBER_ID, DRVER.WHEELCHAIR_FIXING_STTUS_CD, DRVER.LC_CRDNT_X AS LON, DRVER.lC_CRDNT_Y AS LAT FROM ( SELECT MBER_ID, MAX(COLCT_DT) AS COLCT FROM DRVER_LC_HIST WHERE 1=1
AND CNTER_CD = #{cnter_cd}
GROUP BY MBER_ID ) DRVER_LC , DRVER_LC_HIST DRVER WHERE DRVER.MBER_ID = DRVER_LC.MBER_ID AND DRVER_LC.COLCT = DRVER.COLCT_DT )DRVER_LC , (SELECT @rownum:=0)TMP WHERE DRVER.CNTER_CD = CNTER.CNTER_CD AND DRVER.MBER_ID = MBER.MBER_ID AND DRVER.MBER_ID = DRVER_LC.MBER_ID
AND DRVER.CNTER_CD = #{cnter_cd}
AND DRVER.DRVER_STTUS_CD = #{drverSttusCd}
AND MBER.MBER_NM LIKE CONCAT('%', #{drver_name}, '%')
ORDER BY FIELD (DRVER.DRVER_STTUS_CD ,'30', '00', '10', '20', '40'), RCEPT.RESVE_DT DESC )X WHERE @rownum < #{page.limit} ) Y WHERE rnum >= #{page.offset}

