#{list}
GROUP BY MBER_ID ) X , DRVER_LC_HIST DRVER_LC WHERE DRVER_LC.MBER_ID = X.MBER_ID AND DRVER_LC.COLCT_DT = X.COLCT_DT

SELECT DRVER.MBER_ID , DRVER.CNTER_CD , DRVER.LC_CRDNT_X , DRVER.LC_CRDNT_Y , (6371*acos(cos(radians(#{sel_lc_crdnt_x}))*cos(radians(DRVER.LC_CRDNT_X))*cos(radians(DRVER.LC_CRDNT_Y) -radians(#{sel_lc_crdnt_y}))+sin(radians(#{sel_lc_crdnt_x}))*sin(radians(DRVER.LC_CRDNT_X)))) AS distance FROM ( SELECT DRVER_LC.MBER_ID , DRVER_LC.CNTER_CD , DRVER_LC.LC_CRDNT_X , DRVER_LC.LC_CRDNT_Y FROM ( SELECT MAX(COLCT_DT) AS COLCT_DT, MBER_ID FROM DRVER_LC_HIST WHERE MBER_ID IN
#{list}
GROUP BY MBER_ID ) X , DRVER_LC_HIST DRVER_LC WHERE DRVER_LC.MBER_ID = X.MBER_ID AND DRVER_LC.COLCT_DT = X.COLCT_DT ) DRVER HAVING distance <=1 ORDER BY distance LIMIT 0,15

