AND CNTER_CD = #{cnter_cd}
GROUP BY VHCLE_NO, CNTER_CD ) CARALC , RCEPT_HIST RCEPT , MBER_INFO MBER , HANDICAP_INFO HANDICAP , CNTER_INFO CNTER WHERE CARALC.CNTER_CD = RCEPT.CNTER_CD AND CARALC.RESVE_DT = RCEPT.RESVE_DT AND RCEPT.MBER_ID = MBER.MBER_ID AND MBER.MBER_ID = HANDICAP.MBER_ID AND MBER.CNTER_CD = HANDICAP.CNTER_CD AND MBER.CNTER_CD = CNTER.CNTER_CD ) HANDI ON HANDI.VHCLE_NO = DRVER.VHCLE_NO , MBER_INFO MBER WHERE DRVER.MBER_ID = MBER.MBER_ID AND DRVER.MBER_ID IN
#{list}
) X
